document.write("<script language=javascript src='d3.js'></script>");
function force(dataset,selector){
    var width = 500;
    var height = 600;
    var svg = d3.select("#main2")
        .append("svg")
        .attr("width",width)
        .attr("height",height);

    var force = d3.layout.force()
        .nodes(nodes)		//指定节点数组
        .links(edges)		//指定连线数组
        .size([width,height])	//指定范围
        .linkDistance(150)	//指定连线长度
        .charge(-400);	//相互之间的作用力

    force.start();	//开始作用


//添加连线
    var svg_edges = svg.selectAll("line")
        .data(edges)
        .enter()
        .append("line")
        .style("stroke","#ccc")
        .style("stroke-width",1);

    // var color = d3.scale.category20();
    var color = ["#7C5053", "#6B1901", "#B42A01", "#B45A2D", "#B48733", "#D9B318", "#D9D316",
        "#85B02F", "#6EB04A", "#2FA2B0", "#2A87B0", "#35AADE", "#4D6CDE"];

//添加节点
    var svg_nodes = svg.selectAll("circle")
        .data(nodes)
        .enter()
        .append("circle")
        .attr("r",20)
        .style("fill",function(d,i){
            return color[i];
        })
        .on("mouseover", function(d, i){
            //console.log(d)
            if(nodes.name === d.name){
                forceImg.style("fill-opacity",1.0);
            }
        })
        .on("mouseout",function(d,i) {
            if(nodes.name === d.name){
                forceImg.style("fill-opacity",0.0);
            }
        })
        .call(force.drag);	//使得节点能够拖动

//添加描述节点的文字
    var svg_texts = svg.selectAll("text")
        .data(nodes)
        .enter()
        .append("text")
        .style("fill", "black")
        .attr("dx", 20)
        .attr("dy", 8)
        .attr("opacity", 0.0)
        .text(function(d){
            return d.name;
        });

    var edges_text = svg.selectAll(".linetext")
        .data(edges)
        .enter()
        .append("text")
        .attr("class","linetext")
        .style("fill", "black")
        .text(function(d){
            return d.relation;
        });


    force.on("tick", function(){	//对于每一个时间间隔

//更新连线坐标
        svg_edges.attr("x1",function(d){ return d.source.x; })
            .attr("y1",function(d){ return d.source.y; })
            .attr("x2",function(d){ return d.target.x; })
            .attr("y2",function(d){ return d.target.y; });

//更新节点坐标
        svg_nodes.attr("cx",function(d){ return d.x; })
            .attr("cy",function(d){ return d.y; });

//更新文字坐标
        svg_texts.attr("x", function(d){ return d.x; })
            .attr("y", function(d){ return d.y; });

        edges_text.attr("x",function(d){ return (d.source.x + d.target.x) / 2 ; });
        edges_text.attr("y",function(d){ return (d.source.y + d.target.y) / 2 ; });
    });
    // console.log(color);
    var toolBar = document.getElementsByClassName("toolBar")[0];
    var tool = "";
    for(var j=0; j<color.length;j++){
        tool += '<div class="circle1" style="background-color:'+color[j]+';"></div><span class="label2">'+nodes[j].name+'</span>';
    }
    toolBar.innerHTML=tool;
}